export function add_head(e){document.querySelector("head").appendChild(e)}export function add_css(e){let t=document.createElement("style");t.innerHTML=`${e}`,add_head(t)}export function adding_div_child(e){document.createElement("div").innerHTML=`${e}`}export function add_body(e){document.querySelector("body").appendChild(e)}export function google_font(e){for(const t of e.users){let e=document.createElement("link");e.setAttribute("href",`${t.link}`),e.setAttribute("rel","stylesheet"),add_head(e)}}export function file_name_viewer(){let e=window.location.pathname;return e.substring(e.lastIndexOf("/")+1)}export function book_name_recommender(e){let t="";for(let n of e.books)if(n.file_name.includes(`${file_name_viewer()}`))for(let e of n.book)t+=`<a href="${e.template_link}"><img loading="lazy" class="w-100 h-auto p-2"  src="${e.template_image}" alt="${e.image_alt}"/></a>`;if(1==window.location.pathname.includes("scheduling-algorithms"))if(window.innerWidth<=1e3){document.querySelector("#three-section #book-recommendation > div.w-100").innerHTML=`${t}`}else{document.querySelector("#right-content > #book-recommendations > div").innerHTML=`${t}`}if(1==window.location.pathname.includes("Assignment-templates")){document.querySelector("#left-side").innerHTML=`${t}`}}export function sdbar_Links(e){let t=document.querySelector("#left-content > ul");for(const n of e.users)t.innerHTML+=`<li style="list-style: none;">\n    <a style="text-decoration: none;" href="${n.link}">${n.name}</a>\n    </li>`}export function sequence_function_call(...e){for(let t of e)"function"==typeof t&&t()}