export function add_head(e){document.querySelector("head").appendChild(e)}export function add_css(e){let n=document.createElement("style");n.innerHTML=`${e}`,add_head(n)}export function adding_div_child(e){document.createElement("div").innerHTML=`${e}`}export function add_body(e){document.querySelector("body").appendChild(e)}export function google_font(e){for(const n of e.users){let e=document.createElement("link");e.setAttribute("href",`${n.link}`),e.setAttribute("rel","stylesheet"),add_head(e)}}function file_name_viewer(){let e=window.location.pathname;return e.substring(e.lastIndexOf("/")+1)}export function book_name_recommender(e){let n="";for(let t of e.books)if(t.file_name.includes(`${file_name_viewer()}`))for(let e of t.book)n+=`<a href="${e.book_link}"><img loading="lazy" class="w-100 h-auto p-2"  src="${e.book_image}" alt="${e.image_alt}"/></a>`;if(1==window.location.pathname.includes("scheduling-algorithms"))if(window.innerWidth<=1e3){document.querySelector("#three-section #book-recommendation > div.w-100").innerHTML=`${n}`}else{document.querySelector("#right-content > #book-recommendations > div").innerHTML=`${n}`}if(1==window.location.pathname.includes("Assignment-templates")){document.querySelector("#left-side").innerHTML=`${n}`}}export function sdbar_Links(e){let n=document.querySelector("#left-content > ul");for(const t of e.users)n.innerHTML+=`<li style="list-style: none;">\n    <a style="text-decoration: none;" href="${t.link}">${t.name}</a>\n    </li>`}export function sequence_function_call(...e){for(let n of e)"function"==typeof n&&n()}